SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_vig_itens_nf_contrato ( nr_seq_contrato_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_inicial_w		timestamp;
dt_final_w		timestamp;


BEGIN

select	dt_inicio,
	dt_fim
into STRICT	dt_inicial_w,
	dt_final_w
from	contrato
where	nr_sequencia = nr_seq_contrato_p;

if (coalesce(dt_inicial_w::text, '') = '') or (coalesce(dt_final_w::text, '') = '') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265584);
	--'O contrato selecionado não possui vigência definida para atualizar os itens!');
end if;

update	contrato_regra_nf
set	dt_inicio_vigencia = dt_inicial_w,
	dt_fim_vigencia = dt_final_w,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where	nr_seq_contrato = nr_seq_contrato_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
