CREATE OR REPLACE PROCEDURE atualiza_visual_consulta_senha (nm_usuario_p text) AS $body$
DECLARE


nr_seq_next_val_w	bigint;
ie_existe_registro_w	bigint;


BEGIN

begin
	select	1
	into STRICT	ie_existe_registro_w
	from	visualizacao_conulta_senha
	where	nm_usuario = nm_usuario_p;
exception
	when	no_data_found then
		ie_existe_registro_w := 0;
end;

if (ie_existe_registro_w = 0) then
	select	nextval('visualizacao_conulta_senha_seq')
	into STRICT	nr_seq_next_val_w
	;

	insert	into visualizacao_conulta_senha(
		nr_sequencia,
		nm_usuario,
		dt_visualizacao)
	values (nr_seq_next_val_w,
		nm_usuario_p,
		clock_timestamp());
elsif (ie_existe_registro_w = 1) then
	update	visualizacao_conulta_senha
	set	dt_visualizacao = clock_timestamp()
	where	nm_usuario = nm_usuario_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
