SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_volume_hidrico ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, qt_volume_hidrico_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
nr_seq_ancora_w			bigint;
nr_seq_comp_w			bigint;
qt_dose_ancora_w		double precision;
qt_dose_w			double precision;
qt_dose_ml_w			double precision;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_material
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia_solucao = nr_seq_solucao_p
and	ie_agrupador	= 4
and	nr_sequencia	not in (nr_sequencia_w, nr_seq_ancora_w);


BEGIN

if (ie_opcao_p	= 'S') then

	select	coalesce(min(b.nr_sequencia),0)
	into STRICT	nr_seq_ancora_w
	from	material a,
		prescr_material b
	where	b.cd_material		= a.cd_material
	and	b.nr_prescricao		= nr_prescricao_p
	and	b.nr_sequencia_solucao 	= nr_seq_solucao_p
	and	b.ie_agrupador		= 4
	and	a.ie_ancora_solucao	= 'S';

	select	coalesce(min(b.nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	material a,
		prescr_material b
	where	b.cd_material		= a.cd_material
	and	b.nr_prescricao		= nr_prescricao_p
	and	b.nr_sequencia_solucao 	= nr_seq_solucao_p
	and	b.ie_agrupador		= 4
	and	coalesce(a.ie_ancora_solucao,'N')	= 'N';

elsif (ie_opcao_p	= 'T') then

	select	coalesce(min(nr_sequencia),0)
	into STRICT	nr_seq_ancora_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao 	= nr_seq_solucao_p
	and	ie_agrupador		= 4;

	select	coalesce(min(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao 	= nr_seq_solucao_p
	and	ie_agrupador		= 4
	and	nr_sequencia		> nr_seq_ancora_w;

end if;

if (nr_seq_ancora_w	> 0) and (nr_sequencia_w		> 0) then

	select	obter_dose_convertida(cd_material,qt_dose,cd_unidade_medida_dose, obter_unid_med_usua('ml'))
	into STRICT	qt_dose_ancora_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao	= nr_seq_solucao_p
	and	nr_sequencia		= nr_seq_ancora_w;

	qt_dose_ml_w	:= (qt_volume_hidrico_p - qt_dose_ancora_w);

	select obter_dose_convertida(cd_material,qt_dose_ml_w,obter_unid_med_usua('ml'),cd_unidade_medida_dose)
	into STRICT	qt_dose_w
	from	prescr_material
	where	nr_prescricao		= nr_prescricao_p
	and	nr_sequencia_solucao	= nr_seq_solucao_p
	and	nr_sequencia		= nr_sequencia_w;

	if (qt_dose_ml_w < 0) then
		qt_dose_w	:= 0;
		qt_dose_ml_w	:= 0;
	end if;

	update	prescr_material
	set	qt_dose		= qt_dose_w,
		qt_solucao	= qt_dose_ml_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_w;

	open C01;
	loop
	fetch C01 into
		nr_seq_comp_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	prescr_material
		set	qt_dose		= 0,
			qt_solucao	= 0,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_prescricao	= nr_prescricao_p
		and	nr_sequencia	= nr_seq_comp_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_volume_hidrico ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, qt_volume_hidrico_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

