SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atualiza_welcome_letter ( nr_sequencia_p text, ie_welcome_p text) AS $body$
DECLARE



nm_usuario_w		usuario.nm_usuario%type;
ds_lista_w			usuario.ds_texto_email%type;
nr_sequencia_w		usuario.ds_texto_email%type;
nr_pos_virgula_w	episodio_paciente.nr_sequencia%type;


BEGIN
nm_usuario_w := wheb_usuario_pck.get_nm_usuario();



if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	ds_lista_w := nr_sequencia_p;
	while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
		nr_pos_virgula_w := position(',' in ds_lista_w);
		if (nr_pos_virgula_w > 0) then
			nr_sequencia_w := substr(ds_lista_w, 1, nr_pos_virgula_w - 1);
			ds_lista_w := substr(ds_lista_w, nr_pos_virgula_w + 1, length(ds_lista_w));
		else
			nr_sequencia_w := ds_lista_w;
			ds_lista_w := null;
		end if;
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
			update episodio_paciente
			set ie_welcome_letter = ie_welcome_p,
			nm_usuario = nm_usuario_w
			where nr_sequencia = nr_sequencia_w;
		end if;
	end loop;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE atualiza_welcome_letter ( nr_sequencia_p text, ie_welcome_p text) FROM PUBLIC;

