CREATE OR REPLACE PROCEDURE atual_movto_seg_dev_cheque ( NR_SEQ_CHEQUE_P bigint, NM_USUARIO_P text) AS $body$
DECLARE


nr_sequencia_w	bigint;

BEGIN
	select	max(x.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	movto_trans_financ x
	where	x.nr_seq_cheque = nr_seq_cheque_p
	and	exists (SELECT 1
			from	transacao_financeira y
			where	x.nr_seq_trans_financ = y.nr_sequencia
			and	y.ie_acao = 7);

	update	movto_trans_financ
	set	ie_seg_dev_cheque	= 'S'
	where	nr_sequencia	= nr_sequencia_w;

	commit;

end;
