CREATE OR REPLACE PROCEDURE atual_solic_cot_item ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, nr_cot_compra_p bigint) AS $body$
DECLARE


nr_item_cot_compra_w	cot_compra_item.nr_item_cot_compra%type;


BEGIN

select coalesce(min(nr_item_cot_compra),0)
into STRICT nr_item_cot_compra_w
from cot_compra_item
where nr_cot_compra = nr_cot_compra_p
and nr_solic_compra = nr_solic_compra_p
and nr_item_solic_compra = nr_item_solic_compra_p;

if (nr_item_cot_compra_w > 0) then
begin
	update solic_compra_item
	set	nr_cot_compra = nr_cot_compra_p,
		nr_item_cot_compra = nr_item_cot_compra_w
	where	nr_solic_compra = nr_solic_compra_p
		and nr_item_solic_compra = nr_item_solic_compra_p;
end;
end if;

commit;

END;
