CREATE OR REPLACE PROCEDURE atual_superior_etapa_cron_proj (nr_seq_etapa_p bigint, nr_seq_etapa_sup_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w		bigint;
nr_seq_cronograma_ww	bigint;
ds_erro_w				varchar(500);


BEGIN
if (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') and (nr_seq_etapa_sup_p IS NOT NULL AND nr_seq_etapa_sup_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_seq_cronograma),0)
	into STRICT	nr_seq_cronograma_w
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_etapa_p;

	select	coalesce(max(nr_seq_cronograma),0)
	into STRICT	nr_seq_cronograma_ww
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_etapa_sup_p;

	if (nr_seq_cronograma_w > 0) and (nr_seq_cronograma_ww > 0) and (nr_seq_cronograma_w = nr_seq_cronograma_ww) and (nr_seq_etapa_p <> nr_seq_etapa_sup_p) then
		begin
		update	proj_cron_etapa
		set	nr_seq_superior = nr_seq_etapa_sup_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where	nr_sequencia = nr_seq_etapa_p;
		end;
	else
		begin

		ds_erro_w := obter_desc_expressao(780986);/*'Atenção: não foi possível atualizar a etapa superior em relação à etapa selecionada! ' || chr(10) ||
					 'Provavelmente, o código da etapa superior informado é inválido e/ou não corresponde ao mesmo cronograma, ou ainda, ' ||
					 'o código da etapa superior informado corresponde à própria etapa selecionada. ' || chr(10) ||
					 'Favor verificar.';*/
		CALL wheb_mensagem_pck.exibir_mensagem_abort(278178,'DS_ERRO='||ds_erro_w);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
