SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE atual_valores_ret_item_glosa ( nr_seq_ret_item_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


vl_glosa_aceita_w		double precision;
vl_amaior_w		double precision;
vl_amenor_w		double precision;
vl_guia_saldo_w		double precision;
vl_pago_w		double precision;
qt_itens_glosados_w	bigint;
ie_status_retorno_w		varchar(1);


BEGIN

select	coalesce(sum(CASE WHEN coalesce(coalesce(a.ie_acao_glosa,b.ie_acao_glosa), 'R')='A' THEN  vl_glosa  ELSE 0 END ), 0),
	coalesce(sum(vl_amaior), 0),
	count(*)
into STRICT	vl_glosa_aceita_w,
	vl_amaior_w,
	qt_itens_glosados_w
FROM convenio_retorno_glosa a
LEFT OUTER JOIN motivo_glosa b ON (a.cd_motivo_glosa = b.cd_motivo_glosa)
WHERE a.nr_seq_ret_item		= nr_seq_ret_item_p;

select	obter_valor_pend_guia_data(a.nr_interno_conta, a.cd_autorizacao, clock_timestamp()),
	b.ie_status_retorno
into STRICT	vl_guia_saldo_w,
	ie_status_retorno_w
from	convenio_retorno b,
	convenio_retorno_item a
where	a.nr_seq_retorno		= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_ret_item_p;

select	coalesce(sum(a.vl_pago),0)
into STRICT	vl_pago_w
from	convenio_retorno_item a
where	a.nr_sequencia		= nr_seq_ret_item_p;

if (ie_status_retorno_w = 'R') and (qt_itens_glosados_w > 0) then

	vl_amenor_w			:= vl_guia_saldo_w  - vl_pago_w - vl_glosa_aceita_w;

	update	convenio_retorno_item
	set	vl_pago		= vl_guia_saldo_w - vl_amenor_w - vl_glosa_aceita_w,
		vl_amenor	= vl_amenor_w,
		vl_glosado	= vl_glosa_aceita_w,
		vl_adicional	= vl_amaior_w
	where	nr_sequencia	= nr_seq_ret_item_p;

	if (coalesce(ie_commit_p,'S') = 'S') then
		commit;
	end if;

end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
