CREATE OR REPLACE PROCEDURE atulizar_solic_vaga_regulacao ( nr_sequencia_p bigint, ie_solicitacao_p text, ie_tipo_vaga_p text, cd_especialidade_p bigint default null, cd_cid_p text default null, nr_seq_prioridade_p bigint default null, ds_justificativa_p text DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ds_retorno_p INOUT text DEFAULT NULL) AS $body$
DECLARE


ie_solicitacao_w		varchar(10);
ie_tipo_vaga_w			varchar(10);
cd_especialidade_w		bigint;
cd_cid_principal_w		varchar(10);
nr_seq_prioridade_w		bigint;
nr_atendimento_w		bigint;
nr_seq_reg_atend_w		regulacao_atend.nr_sequencia%type;
ie_consistencia_w		varchar(255);


BEGIN
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		begin
			select max(nr_sequencia)
			into STRICT nr_seq_reg_atend_w
			from regulacao_atend
			where nr_seq_gestao_vaga = nr_sequencia_p;

			if (nr_seq_reg_atend_w IS NOT NULL AND nr_seq_reg_atend_w::text <> '') then
				begin
					select ie_solicitacao,
							  ie_tipo_vaga,
							  cd_especialidade,
							  cd_cid_principal,
							  nr_seq_prioridade,
							  nr_atendimento
					into STRICT ie_solicitacao_w,
							ie_tipo_vaga_w,
							cd_especialidade_w,
							cd_cid_principal_w,
							nr_seq_prioridade_w,
							nr_atendimento_w
					from gestao_vaga
					where nr_sequencia = nr_sequencia_p;

					if (ie_solicitacao_p <> ie_solicitacao_w)
						or (ie_tipo_vaga_p <> ie_tipo_vaga_w)
						or (cd_especialidade_p <> cd_especialidade_w)
						or (cd_cid_p <> cd_cid_principal_w) then
						begin
							CALL alterar_status_regulacao(nr_seq_reg_atend_w, 'CA', obter_desc_expressao(930485), obter_nome_usuario(nm_usuario_p));

							update gestao_vaga
							set ie_solicitacao = ie_solicitacao_p,
							  ie_tipo_vaga = ie_tipo_vaga_p,
							  cd_especialidade = cd_especialidade_p,
							  cd_cid_principal = cd_cid_p,
							  nr_seq_prioridade = nr_seq_prioridade_p
							where nr_sequencia = nr_sequencia_p;

							commit;

							ie_consistencia_w := consistir_regulacao(nr_atendimento_w, nr_sequencia_p, 'GESTAO_VAGA', ie_consistencia_w);

							if (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
								CALL gerar_hist_alteracao_status(nr_sequencia_p, null, null, ds_justificativa_p, nm_usuario_p);
							end if;

							ds_retorno_p := 'SUCCESS';
						end;
					elsif (nr_seq_prioridade_p <> nr_seq_prioridade_w) then
						update gestao_vaga
						set nr_seq_prioridade = nr_seq_prioridade_p
						where nr_sequencia = nr_sequencia_p;

						commit;

						CALL alterar_prioridade_regulacao(nr_seq_reg_atend_w, nr_seq_prioridade_p, obter_desc_expressao(667027), ds_justificativa_p);

						if (ds_justificativa_p IS NOT NULL AND ds_justificativa_p::text <> '') then
							CALL gerar_hist_alteracao_status(nr_sequencia_p, null, null, ds_justificativa_p, nm_usuario_p);
						end if;

						ds_retorno_p := 'SUCCESS';
					end if;
				end;
			end if;
		end;
	end if;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
