	(select nvl(obter_dados_operacao_nota(y.cd_operacao_nf, 10), ''N'') from nota_fiscal y where y.nr_sequencia = (select max(v.nr_sequencia_ref) from nota_fiscal v where 	v.nr_sequencia = :nr_seq_nf)) = ''N'')
union
