75042, 5, 102356, 'refNFe', 'refNFe', NULL, 'select 	distinct nvl(a.nr_danfe, (select max(nr_danfe_referencia) from nota_fiscal z where z.nr_sequencia = :nr_seq_nf)) refNFe
from   	nota_fiscal a
where   ((a.nr_sequencia in (select b.nr_seq_nf_orig from nota_fiscal_item b where b.nr_sequencia = :nr_seq_nf)) or
	(((select max(b.nr_seq_nf_orig) from nota_fiscal_item b where b.nr_sequencia = :nr_seq_nf) is null) and a.nr_sequencia = :nr_seq_nf))
and 	(select	nvl(max(o.ie_tipo_cfop),1) from nota_fiscal x, natureza_operacao o where x.nr_sequencia = :nr_seq_nf and x.cd_natureza_operacao = o.cd_natureza_operacao) in (2,4)
and 	(((select max(p.nr_sequencia_ref) from nota_fiscal p where p.nr_sequencia = :nr_seq_nf) is null) or
	(select nvl(obter_dados_operacao_nota(y.cd_operacao_nf, 10), ''N'') from nota_fiscal y where y.nr_sequencia = (select max(v.nr_sequencia_ref) from nota_fiscal v where 		v.nr_sequencia = :nr_seq_nf)) = ''N'')'
, NULL, 'apapandini',  TO_Date( '10/23/2018 03:50:44 ', 'MM/DD/YYYY HH:MI:SS')
