DS_SQL = 'select	max(orig) orig,
	''00'' CST,
	0 modBC,
	max(vBC) vBC,
	max(pICMS) pICMS,
	max(vICMS) vICMS
from	(	select	to_char(a.vl_base_calculo ,''FM99999990.0099'') vBC,
			to_char(a.tx_tributo,''FM99999990.0099'') pICMS,
			to_char(a.vl_tributo,''FM99990.0099'') vICMS,
			b.ie_tributacao_icms,
			nvl(b.ie_origem_mercadoria,''0'')  orig
		from	NOTA_FISCAL_ITEM_TRIB a,
			material_fiscal b,
			nota_fiscal_item c,
			tributo d
		where	c.nr_item_nf = a.nr_item_nf
		and	c.nr_sequencia = a.nr_sequencia
		and	a.cd_tributo = d.cd_tributo
		and	c.cd_material = b.cd_material
		and	a.nr_sequencia = :nr_seq_nf
		and	a.nr_item_nf  = :nr_item_nf
		and	b.ie_tributacao_icms = ''00''
		and	d.ie_tipo_tributo = ''ICMS''
		union all
		select	''0'' vBC,
			''0'' pICMS,
			''0'' vICMS,
			b.ie_tributacao_icms,
			nvl(b.ie_origem_mercadoria,''0'')  orig
		from	material_fiscal b,
			nota_fiscal_item c
		where	c.cd_material = b.cd_material
		and	c.nr_sequencia = :nr_seq_nf
		and	c.nr_item_nf  = :nr_item_nf
		and	b.ie_tributacao_icms = ''00'')
where ie_tributacao_icms = ''00''
group by ie_tributacao_icms',
