SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE autorizar_autorizacoes_agenda (nr_seq_estagio_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_interno_w		varchar(2);
ie_atualizar_qtdade_w	varchar(1);
			
c01 CURSOR FOR
SELECT	a.nr_sequencia
from	autorizacao_convenio a,
	estagio_autorizacao b
where	a.nr_seq_estagio	= b.nr_sequencia
and	b.ie_interno		= '1' --Necessidade
and	a.nr_seq_agenda		= nr_seq_agenda_p
and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = b.cd_empresa;			
			

BEGIN

select	max(ie_interno)
into STRICT	ie_interno_w
from	estagio_autorizacao
where	nr_sequencia	= nr_seq_estagio_p
and	OBTER_EMPRESA_ESTAB(wheb_usuario_pck.get_cd_estabelecimento) = cd_empresa;

ie_atualizar_qtdade_w := obter_param_usuario(3004, 17, wheb_usuario_pck.get_cd_perfil, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_atualizar_qtdade_w);

if (ie_interno_w = '10') then

	open C01;
	loop
	fetch C01 into	
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (ie_atualizar_qtdade_w = 'S') then
		
			update	procedimento_autorizado
			set	qt_autorizada		= qt_solicitada,
				dt_atualizacao 		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	nr_sequencia_autor	= nr_sequencia_w;
			
			update	material_autorizado
			set	qt_autorizada		= qt_solicitada,
				dt_atualizacao 		= clock_timestamp(),
				nm_usuario		= nm_usuario_p
			where	nr_sequencia_autor	= nr_sequencia_w;
		
		end if;		
		
		CALL atualizar_autorizacao_convenio( nr_sequencia_w, nm_usuario_p, nr_seq_estagio_p, 'N', 'N', 'N');
		
		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
