CREATE OR REPLACE PROCEDURE avanca_retrocede_estagio (nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_status_w ESTAGIO_STATUS_ENTREGA.NR_SEQ_STATUS%TYPE;
ie_status_execucao_w STATUS_REGRA_ENTREGA.IE_STATUS_EXECUCAO%TYPE;
nr_seq_estagio_w ESTAGIO_STATUS_ENTREGA.NR_SEQUENCIA%TYPE;
nr_sequencia_aux_w bigint;
prox_estagio_w bigint;
estagio_ant_w bigint;
nr_seq_prescr_proc_compl_w PRESCR_PROCEDIMENTO_COMPL.NR_SEQUENCIA%TYPE;


BEGIN

select prescr_proc.nr_seq_proc_compl,
       prescr_proc_compl.nr_seq_estagio_entrega,
       prescr_proc.ie_status_execucao
into STRICT nr_seq_prescr_proc_compl_w,
     nr_seq_estagio_w,
     ie_status_execucao_w
from prescr_procedimento prescr_proc
     left join prescr_procedimento_compl prescr_proc_compl ON (prescr_proc.nr_seq_proc_compl = prescr_proc_compl.nr_sequencia)
where prescr_proc.nr_prescricao = nr_prescricao_p
and   prescr_proc.nr_sequencia = nr_seq_prescricao_p;

if (coalesce(nr_seq_prescr_proc_compl_w::text, '') = '') then
  CALL gerar_prescr_proc_compl(nr_prescricao_p, nr_seq_prescricao_p, nm_usuario_p, 'N');
  select prescr_proc.nr_seq_proc_compl
  into STRICT nr_seq_prescr_proc_compl_w
  from prescr_procedimento prescr_proc
  where prescr_proc.nr_prescricao = nr_prescricao_p
  and   prescr_proc.nr_sequencia = nr_seq_prescricao_p;
end if;

if (coalesce(nr_seq_estagio_w::text, '') = '') then
  nr_seq_estagio_w := obter_estagio_inicial(obter_regra_entrega_apropriada(nr_prescricao_p, nr_seq_prescricao_p), ie_status_execucao_w);
end if;

select NR_SEQ_STATUS
into STRICT nr_status_w
from ESTAGIO_STATUS_ENTREGA
where nr_sequencia = nr_seq_estagio_w;

select prox_estagio, estagio_ant
   into STRICT prox_estagio_w, estagio_ant_w
   from (
      SELECT nr_sequencia, lead(nr_sequencia, 1) over (order by nr_seq_ordem, nr_sequencia) as prox_estagio,
             lag(nr_sequencia, 1) over (order by nr_seq_ordem, nr_sequencia) as estagio_ant
      from estagio_status_entrega
      where nr_seq_status = nr_status_w
   ) alias4 where nr_sequencia = nr_seq_estagio_w;

if (ie_opcao_p = 'A') then
   nr_sequencia_aux_w := prox_estagio_w;
elsif (ie_opcao_p = 'R') then
   nr_sequencia_aux_w := estagio_ant_w;
end if;

update prescr_procedimento_compl
set nr_seq_estagio_entrega = nr_sequencia_aux_w,
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
where nr_sequencia = nr_seq_prescr_proc_compl_w;

CALL gravar_auditoria_entrega(nr_sequencia_aux_w,
						 nr_prescricao_p,
						 nr_seq_prescricao_p,
						 nm_usuario_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
