CREATE OR REPLACE PROCEDURE avisar_agenda_horario_js ( nr_seq_escala_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE


qt_agenda_w	bigint;
ds_mensagem_w	varchar(255) := '';


BEGIN

if (nr_seq_escala_p IS NOT NULL AND nr_seq_escala_p::text <> '')then
	begin

	select 	count(*)
	into STRICT	qt_agenda_w
	from	escala a,
		escala_grupo b,
		escala_grupo_agenda c
	where 	a.nr_sequencia  = nr_seq_escala_p
	and	a.nr_seq_grupo	= b.nr_sequencia
	and	c.nr_seq_grupo	= b.nr_sequencia;

	if (qt_agenda_w = 0)then
		begin

		ds_mensagem_w	:= obter_texto_tasy(47040, 1);

		end;

	end if;

	end;
end if;

ds_mensagem_p	:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
