SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE avisar_aprovacao_pendente_sme (nr_seq_ordem_serv_p man_ordem_serv_impacto.nr_seq_ordem_serv%type, nr_seq_impacto_p man_ordem_serv_impacto.nr_sequencia%type) AS $body$
DECLARE


ds_email_titulo_w	varchar(255);
ds_email_corpo_w	varchar(255);
ds_email_ori_w	usuario.ds_email%type;

c01 CURSOR FOR
	SELECT	distinct substr(obter_nome_pf(b.cd_pessoa_fisica),1,80) nm_pessoa_fisica,
		x.ds_email
	from	man_ordem_serv_imp_resp a,
		sme_equipe_regra b,
		usuario x
	where	b.nr_seq_sme_equipe	= a.nr_seq_sme_equipe
	and	x.cd_pessoa_fisica	= b.cd_pessoa_fisica
	and	a.nr_seq_impacto	= nr_seq_impacto_p
	and	coalesce(b.ie_situacao, 'A')	= 'A'
	and	coalesce(a.ie_resposta, 'N') = 'S'
	and	coalesce(a.dt_aprovacao::text, '') = '';
	
BEGIN

if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '' AND nr_seq_impacto_p IS NOT NULL AND nr_seq_impacto_p::text <> '') then
	for r_c01 in c01 loop
		begin	
			select	coalesce(max(x.ds_email), 'support.informatics@philips.com')
			into STRICT	ds_email_ori_w
			from	man_ordem_serv_impacto a,
				usuario x
			where 	x.nm_usuario 		= a.nm_usuario
			and	nr_seq_ordem_serv 	= nr_seq_ordem_serv_p;	
			
			if (ds_email_ori_w IS NOT NULL AND ds_email_ori_w::text <> '') and (r_c01.ds_email IS NOT NULL AND r_c01.ds_email::text <> '') then
			
				ds_email_titulo_w	:= obter_desc_expressao(953826);
				ds_email_corpo_w	:= replace(replace(obter_desc_expressao(953857), '#@NM_PESSOA_FISICA#@', r_c01.nm_pessoa_fisica), '#@NR_SEQ_ORDEM_SERV#@', nr_seq_ordem_serv_p);
				CALL enviar_email(ds_email_titulo_w, ds_email_corpo_w, ds_email_ori_w, r_c01.ds_email, 'Tasy', 'M');

				CALL gravar_log_tasy(1516, nr_seq_ordem_serv_p || '-' || r_c01.nm_pessoa_fisica || '-' || r_c01.ds_email, 'Tasy');			
			end if;
		exception
			when others then
				CALL gravar_log_tasy(1415, r_c01.nm_pessoa_fisica, 'Tasy');
		end;
	end loop;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
