SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE avisar_justificativa_nf ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
nr_nota_fiscal_w			varchar(255);
cd_material_w			integer;
cd_material_ww			integer;
nr_ordem_compra_w		numeric(20);
nr_seq_classif_w			bigint;
qt_usuario_w			bigint;
cd_cgc_fornecedor_w		numeric(20);
cd_comprador_w			varchar(10);
nm_fornecedor_w			varchar(254);
ds_material_w			varchar(255);
ds_titulo_w			varchar(255);
ds_justificativa_w			varchar(255);
ds_relacao_materiais_w		varchar(4000);
ds_titulo_materiais_w		varchar(255);
ds_comunicado_w			varchar(4000);
nm_usuario_destino_w		varchar(2000);
nm_usuario_delegacao_w		varchar(15);
nm_usuario_comprador_w		varchar(15);
qt_pos_usuario_w			integer;
cd_estabelecimento_w		smallint;
nm_usuarios_comunic_diver_w	varchar(255);
ie_diverg_oc_nf_w			varchar(1);
ds_tipo_divergencia_w		varchar(255);

C01 CURSOR FOR 
SELECT	distinct 
	a.nr_ordem_compra 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a 
where	a.nr_seq_nf = b.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p;

C02 CURSOR FOR 
SELECT	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,80) ds_material, 
	b.ds_justificativa, 
	substr(obter_valor_dominio(97,a.cd_tipo_divergencia),1,255) ds_tipo_divergencia 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and (b.ds_justificativa IS NOT NULL AND b.ds_justificativa::text <> '') 
and	((ie_diverg_oc_nf_w <> 'S') or (ie_diverg_oc_nf_w = 'S' and (b.ds_justificativa IS NOT NULL AND b.ds_justificativa::text <> ''))) 

union
 
SELECT	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,80),			  
	substr(obter_descricao_padrao('SUP_TIPO_JUSTIFICATIVA','DS_TIPO_JUSTIFICATIVA',c.nr_seq_tipo_justificativa),1,80), 
	substr(obter_valor_dominio(97,c.cd_tipo_divergencia),1,255) ds_tipo_divergencia 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a, 
	divergencia_oci_justif c 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	c.nr_seq_oci_just = a.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and	(c.nr_seq_tipo_justificativa IS NOT NULL AND c.nr_seq_tipo_justificativa::text <> '') 

union
 
select	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,255),			  
	substr(c.ds_justificativa,1,80), 
	substr(obter_valor_dominio(97,c.cd_tipo_divergencia),1,255) ds_tipo_divergencia 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a, 
	divergencia_oci_justif c 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	c.nr_seq_oci_just = a.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and 	(c.ds_justificativa IS NOT NULL AND c.ds_justificativa::text <> '') 
order by 2;


BEGIN 
 
ds_relacao_materiais_w		:= null;
nm_usuario_destino_w		:= null;
ds_titulo_w			:= WHEB_MENSAGEM_PCK.get_texto(297703);
cd_material_ww			:= 0;
 
select	nr_nota_fiscal, 
	cd_estabelecimento 
into STRICT	nr_nota_fiscal_w, 
	cd_estabelecimento_w 
from	nota_fiscal 
where	nr_sequencia = nr_sequencia_p;
 
select	max(nm_usuarios_comunic_diver), 
		coalesce(max(ie_diverg_oc_nf),'N') 
into STRICT	nm_usuarios_comunic_diver_w, 
		ie_diverg_oc_nf_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
open	C01;
loop 
fetch	C01 into 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		 
	ds_relacao_materiais_w := null;
	 
	select	substr(obter_dados_pf_pj(null,cd_cgc_fornecedor,'N'),1,254), 
		substr(obter_usuario_pessoa(cd_comprador),1,15), 
		substr(obter_usuario_pessoa(obter_pessoa_delegacao(cd_comprador,'RC',clock_timestamp())),1,15) 
	into STRICT	nm_fornecedor_w, 
		nm_usuario_comprador_w, 
		nm_usuario_delegacao_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w;
 
	if (nm_usuario_delegacao_w IS NOT NULL AND nm_usuario_delegacao_w::text <> '') then 
			nm_usuario_comprador_w := nm_usuario_comprador_w || ',' || nm_usuario_delegacao_w;
	end if;
 
	select	coalesce(position(nm_usuario_comprador_w in nm_usuario_destino_w),0) 
	into STRICT	qt_pos_usuario_w 
	;
 
	if (nm_usuario_comprador_w IS NOT NULL AND nm_usuario_comprador_w::text <> '') and (qt_pos_usuario_w = 0) then 
		nm_usuario_destino_w	:= nm_usuario_destino_w || nm_usuario_comprador_w || ', ';
										 
		ds_comunicado_w		:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297705,'DS_COMUNICADO_W=' || ' ' || ';' || 
										'NM_FORNECEDOR_W=' || nm_fornecedor_w),1,4000);
		ds_comunicado_w		:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297706,'DS_COMUNICADO_W=' || ' ' || ';' || 
										'NR_ORDEM_COMPRA_W=' || to_char(nr_ordem_compra_w)),1,4000);
		ds_comunicado_w 	:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297707,'DS_COMUNICADO_W=' || ' ' || ';' || 
										'NR_NOTA_FISCAL_W=' || nr_nota_fiscal_w),1,4000);
	end if;
 
	open	C02;
	loop 
	fetch	C02 into 
		cd_material_w, 
		ds_material_w, 
		ds_justificativa_w, 
		ds_tipo_divergencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		if (cd_material_ww <> cd_material_w) then 
			 
			ds_comunicado_w	:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297708,'DS_COMUNICADO_W=' || ' ' || ';' || 'DS_MATERIAL_W=' || ds_material_w),1,4000);
			ds_comunicado_w	:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297709,'DS_COMUNICADO_W=' || ' ' || ';' || 'DS_TIPO_DIVERGENCIA_W=' || ds_tipo_divergencia_w),1,4000);
			ds_comunicado_w	:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297710,'DS_COMUNICADO_W=' || ' ' || ';' || 'DS_JUSTIFICATIVA_W=' || ds_justificativa_w),1,4000);
			cd_material_ww	:= cd_material_w;
		end if;
	end loop;
	close C02;
end;
end loop;
close	C01;
 
nm_usuario_destino_w := nm_usuario_destino_w || nm_usuarios_comunic_diver_w;
 
if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') and (ds_comunicado_w IS NOT NULL AND ds_comunicado_w::text <> '') then 
	select	nextval('comunic_interna_seq') 
	into STRICT	nr_sequencia_w 
	;
 
	select	obter_classif_comunic('F') 
	into STRICT	nr_seq_classif_w 
	;
 
	insert into comunic_interna( 
		dt_comunicado, ds_titulo, ds_comunicado, nm_usuario, 
		dt_atualizacao, ie_geral, nm_usuario_destino, nr_sequencia, 
		ie_gerencial, nr_seq_classif, dt_liberacao) 
	values ( 
		clock_timestamp() + interval '1 days'/86400, ds_titulo_w, ds_comunicado_w, WHEB_MENSAGEM_PCK.get_texto(297711), 
		clock_timestamp(), 'N', nm_usuario_destino_w, nr_sequencia_w, 'N', 
		nr_seq_classif_w, clock_timestamp());
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE avisar_justificativa_nf ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

