SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE avisar_justif_email_nf_oc ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
nr_nota_fiscal_w			varchar(255);
cd_material_w			integer;
cd_material_ww			integer;
nr_ordem_compra_w		numeric(20);
nr_seq_classif_w			bigint;
qt_usuario_w			bigint;
cd_cgc_fornecedor_w		numeric(20);
nm_fornecedor_w			varchar(254);
ds_material_w			varchar(255);
ds_titulo_w			varchar(255);
ds_justificativa_w			varchar(255);
ds_titulo_materiais_w		varchar(255);
ds_comunicado_w			varchar(2000);
nm_usuario_delegacao_w		varchar(15);
nm_usuario_comprador_w		varchar(15);
qt_pos_usuario_w			integer;
cd_estabelecimento_w		smallint;
nm_usuarios_comunic_diver_w	varchar(255);
ie_diverg_oc_nf_w			varchar(1);
ds_email_remetente_w		varchar(255);

/* Email destino ( Comprador ) */
 
ds_email_dest_comprador_w		varchar(255);
ds_mensagem_w			varchar(2000);
ds_assunto_w			varchar(255);

/* Número da ordem de compra para verificação se é enviado o email */
 
nr_ordem_compra_email_w		numeric(20);

/* Informações da regra */
 
nr_seq_regra_w			bigint;
ds_email_adicional_w		varchar(2000);
cd_perfil_dispara_w			integer;
ie_momento_envio_w		varchar(1);

/* Referente ao usúario e email origem */
 
ie_usuario_w			varchar(3);
ds_email_origem_w			varchar(255);
ds_usuario_origem_w		varchar(255);

/* Informações do comprador */
 
cd_comprador_w			varchar(10);
ds_email_comprador_w		varchar(255);
ds_usuario_comprador_w   		varchar(255);

/* Caso haja mais de uma ordem de compra pega a última e assim o último comprador */
 
cd_comprador_email_origem_w varchar(10);
ds_email_comprador_orig_w	varchar(255);
ds_usuario_comprador_orig_w	varchar(255);

qt_existe_w			bigint;

/* Campos da macro */
 
nr_ordem_compra_macro_w		varchar(255) := '';
cd_material_macro_w		varchar(255) := '';
ds_material_macro_w		varchar(255) := '';
ds_justificativa_macro_w		varchar(255) := '';
nm_fornecedor_macro_w		varchar(255) := '';

C01 CURSOR FOR 
SELECT	distinct 
	a.nr_ordem_compra 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a 
where	a.nr_seq_nf = b.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p;

C02 CURSOR FOR 
SELECT	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,80) ds_material, 
	b.ds_justificativa 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and ((ie_diverg_oc_nf_w <> 'S') or (ie_diverg_oc_nf_w = 'S' and (b.ds_justificativa IS NOT NULL AND b.ds_justificativa::text <> ''))) 

union
 
SELECT	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,80), 
	substr(obter_descricao_padrao('SUP_TIPO_JUSTIFICATIVA','DS_TIPO_JUSTIFICATIVA',c.nr_seq_tipo_justificativa),1,80) 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a, 
	divergencia_oci_justif c 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	c.nr_seq_oci_just = a.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and	(c.nr_seq_tipo_justificativa IS NOT NULL AND c.nr_seq_tipo_justificativa::text <> '') 

union
 
select	b.cd_material, 
	substr(obter_desc_material(b.cd_material),1,255), 
	substr(c.ds_justificativa,1,80) 
from	nota_fiscal_item b, 
	divergencia_ordem_compra_item a, 
	divergencia_oci_justif c 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	a.nr_item_doi_nfe = b.nr_item_oci 
and	a.nr_seq_nf = b.nr_sequencia 
and	c.nr_seq_oci_just = a.nr_sequencia 
and	a.nr_seq_nf = nr_sequencia_p 
and	a.nr_ordem_compra = nr_ordem_compra_w 
and 	(c.ds_justificativa IS NOT NULL AND c.ds_justificativa::text <> '') 
order by 2;


BEGIN 
 
ds_titulo_w				:= WHEB_MENSAGEM_PCK.get_texto(297686);
cd_material_ww				:= 0;
 
select	nr_nota_fiscal, 
	cd_estabelecimento 
into STRICT	nr_nota_fiscal_w, 
	cd_estabelecimento_w 
from	nota_fiscal 
where	nr_sequencia = nr_sequencia_p;
 
select	coalesce(max(ie_diverg_oc_nf),'N') 
into STRICT	ie_diverg_oc_nf_w 
from	parametro_compras 
where	cd_estabelecimento = cd_estabelecimento_w;
 
open	C01;
loop 
fetch	C01 into 
	nr_ordem_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	nr_ordem_compra_macro_w := substr(nr_ordem_compra_macro_w || ',' || nr_ordem_compra_w,1,255);
 
	select	coalesce(substr(obter_dados_pf_pj(null,cd_cgc_fornecedor,'N'),1,254),'X'), 
		coalesce(substr(obter_usuario_pessoa(cd_comprador),1,15),'X'), 
		coalesce(substr(obter_usuario_pessoa(obter_pessoa_delegacao(cd_comprador,'RC',clock_timestamp())),1,15),'X'), 
		coalesce(cd_comprador,'X') 
	into STRICT	nm_fornecedor_w, 
		nm_usuario_comprador_w, 
		nm_usuario_delegacao_w, 
		cd_comprador_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w;
 
	if (cd_comprador_w <> 'X') then 
		select	ds_email, 
			nm_guerra 
		into STRICT	ds_email_comprador_w, 
			ds_usuario_comprador_w 
		from	comprador 
		where	cd_pessoa_fisica		= cd_comprador_w 
		and	cd_estabelecimento		= cd_estabelecimento_w;
	end if;
 
	nm_fornecedor_macro_w := substr(nm_fornecedor_macro_w || ', ' || nm_fornecedor_w,1,255);
 
	select	coalesce(position(ds_email_comprador_w in ds_email_dest_comprador_w),0) 
	into STRICT	qt_pos_usuario_w 
	;
 
	if (ds_email_comprador_w IS NOT NULL AND ds_email_comprador_w::text <> '') and (qt_pos_usuario_w = 0) then 
		ds_email_dest_comprador_w	:= substr(ds_email_dest_comprador_w || ds_email_comprador_w || ';',1,255);
		 
		ds_comunicado_w			:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297692,'DS_COMUNICADO_W=' || ' ' || ';' || 
											'NM_FORNECEDOR_W=' || nm_fornecedor_w),1,2000);
							 
		ds_comunicado_w			:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297693,'DS_COMUNICADO_W=' || ' ' || ';' || 
											'NR_ORDEM_COMPRA_W=' || to_char(nr_ordem_compra_w)),1,2000);
											 
		ds_comunicado_w 		:= substr(ds_comunicado_w || WHEB_MENSAGEM_PCK.get_texto(297695,'DS_COMUNICADO_W=' || ' ' || ';' || 
											'NR_NOTA_FISCAL_W=' || nr_nota_fiscal_w),1,2000);
	end if;
 
	open	C02;
	loop 
	fetch	C02 into 
		cd_material_w, 
		ds_material_w, 
		ds_justificativa_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
 
		cd_material_macro_w 	:= substr(cd_material_macro_w || ', ' || cd_material_w,1,255);
		ds_material_macro_w	:= substr(ds_material_macro_w || ', ' || ds_material_w,1,255);
		ds_justificativa_macro_w	:= substr(ds_justificativa_macro_w || ', ' || ds_justificativa_w,1,255);
 
		if (cd_material_ww <> cd_material_w) then 
			ds_comunicado_w	:= substr(ds_comunicado_w || ds_material_w || chr(13) || chr(10),1,2000);
			cd_material_ww	:= cd_material_w;
		end if;
 
		ds_comunicado_w := substr(ds_comunicado_w || '	' || ds_justificativa_w || chr(13) || chr(10),1,2000);
 
	end loop;
	close C02;
 
	end;
end loop;
close	C01;
 
/*  Verifica a regra e envia email */
 
 
select	coalesce(max(nr_sequencia),0), 
	max(replace(ds_email_adicional,',',';')), 
	coalesce(max(ds_email_remetente),'X'), 
	max(cd_perfil_disparar), 
	coalesce(max(ie_momento_envio),'I') 
into STRICT	nr_seq_regra_w, 
	ds_email_adicional_w, 
	ds_email_remetente_w, 
	cd_perfil_dispara_w, 
	ie_momento_envio_w 
from	regra_envio_email_compra 
where	ie_tipo_mensagem = 34 
and	ie_situacao = 'A' 
and	cd_estabelecimento = cd_estabelecimento_w 
and	obter_se_envia_email_regra(nr_Sequencia_p, 'NF', 34, cd_Estabelecimento_w) = 'S';
 
if (nr_seq_regra_w > 0) and 
	((coalesce(cd_perfil_dispara_w::text, '') = '') or 
	(cd_perfil_dispara_w IS NOT NULL AND cd_perfil_dispara_w::text <> '' AND cd_perfil_dispara_w = obter_perfil_ativo)) then 
	begin 
	 
	select ds_assunto, 
		ds_mensagem_padrao 
	into STRICT	ds_assunto_w, 
		ds_mensagem_w 
	from	regra_envio_email_compra 
	where	nr_sequencia = nr_seq_regra_w;
 
	ds_assunto_w := substr(replace_macro(ds_assunto_w,'@nr_nota_fiscal', nr_nota_fiscal_w),1,255);
	ds_assunto_w := substr(replace_macro(ds_assunto_w,'@ordem',nr_ordem_compra_macro_w),1,255);
	ds_assunto_w := substr(replace_macro(ds_assunto_w,'@material',ds_material_macro_w),1,255);
	ds_assunto_w := substr(replace_macro(ds_assunto_w,'@justificativa',ds_justificativa_macro_w),1,255);
	ds_assunto_w := substr(replace_macro(ds_assunto_w,'@fornecedor',nm_fornecedor_macro_w),1,255);
	 
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w,'@nr_nota_fiscal', nr_nota_fiscal_w),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w,'@ordem',nr_ordem_compra_macro_w),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w,'@material',ds_material_macro_w),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w,'@justificativa',ds_justificativa_macro_w),1,2000);
	ds_mensagem_w := substr(replace_macro(ds_mensagem_w,'@fornecedor',nm_fornecedor_macro_w),1,2000);
 
	select	coalesce(max(ie_usuario),'U') 
	into STRICT	ie_usuario_w 
	from	regra_envio_email_compra 
	where	nr_sequencia = nr_seq_regra_w;
 
	if (ie_usuario_w = 'U') then --Usuario 
		select	ds_email, 
			nm_usuario 
		into STRICT	ds_email_origem_w, 
			ds_usuario_origem_w 
		from	usuario 
		where	nm_usuario = nm_usuario_p;
	elsif (ie_usuario_w = 'C') then --Setor compras 
		select	ds_email 
		into STRICT	ds_email_origem_w 
		from	parametro_compras 
		where	cd_estabelecimento = cd_estabelecimento_w;
 
		select	coalesce(ds_fantasia,ds_razao_social) 
		into STRICT	ds_usuario_origem_w 
		from	estabelecimento_v 
		where	cd_estabelecimento = cd_estabelecimento_w;
	elsif (ie_usuario_w = 'O') then --Comprador 
			begin 
 
			select	coalesce(max(a.nr_ordem_compra),0) 
			into STRICT	nr_ordem_compra_email_w 
			from	nota_fiscal_item a 
			where	a.nr_sequencia = nr_sequencia_p;
 
			if (coalesce(nr_ordem_compra_email_w,0) <> 0) then 
				begin 
 
				select	coalesce(cd_comprador,'X') 
				into STRICT	cd_comprador_email_origem_w 
				from	ordem_compra 
				where	nr_ordem_compra = nr_ordem_compra_email_w;
 
				if (cd_comprador_email_origem_w <> 'X') then 
				select	ds_email, 
					nm_guerra 
				into STRICT	ds_email_comprador_orig_w, 
					ds_usuario_comprador_orig_w 
				from	comprador 
				where	cd_pessoa_fisica		= cd_comprador_email_origem_w 
				and	cd_estabelecimento		= cd_estabelecimento_w;
				end if;
 
				ds_email_origem_w		:= ds_email_comprador_orig_w;
				ds_usuario_origem_w	:= ds_usuario_comprador_orig_w;
 
				end;
			else 
				ds_email_origem_w		:= nm_usuario_p;
				ds_usuario_origem_w	:= '';
 
			end if;
 
			end;
	end if;
 
	if (ds_email_adicional_w IS NOT NULL AND ds_email_adicional_w::text <> '') then 
		ds_email_dest_comprador_w := substr(ds_email_dest_comprador_w || ds_email_adicional_w,1,255);
	end if;
 
	if (ds_email_remetente_w <> 'X') then 
		ds_email_origem_w	:= ds_email_remetente_w;
	end if;
 
	/*select	count(*) 
	into	qt_existe_w 
	from 	dual 
	where	obter_se_envia_email_internet(34,null,cd_estabelecimento_w,nr_ordem_compra_email_w) = 'S' 
	and	obter_se_envia_email_ordem(34,nr_ordem_compra_email_w,cd_estabelecimento_w) = 'S';*/
	 
 
	if (coalesce(ds_email_dest_comprador_w,'X') <> 'X') then 
		begin 
 
		if (ie_momento_envio_w = 'A') then 
			begin 
 
			CALL sup_grava_envio_email( 
				'NF', 
				'34', 
				nr_nota_fiscal_w, 
				null, 
				null, 
				ds_email_dest_comprador_w, 
				ds_usuario_origem_w, 
				ds_email_origem_w, 
				ds_assunto_w, 
				ds_mensagem_w, 
				cd_estabelecimento_w, 
				nm_usuario_p, 
				'N');
 
			end;
		else 
			begin 
			ds_mensagem_w	:= substr(ds_mensagem_w || chr(13) || chr(10) || ds_comunicado_w,1,2000);
			CALL enviar_email(ds_assunto_w,ds_mensagem_w,ds_email_origem_w,ds_email_dest_comprador_w,ds_usuario_origem_w,'M');
			exception when others then 
				ds_assunto_w := '';
			end;
		end if;
 
		end;
	end if;	
 
	end;
end if;
 
/*commit; - Não pode ter commit*/
 
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE avisar_justif_email_nf_oc ( nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

