SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE avisar_venc_contrato_doc ( cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_pessoa_responsavel_w		contrato.cd_pessoa_resp%type;
nr_sequencia_w				bigint;
nr_seq_classif_w				bigint;
cd_contrato_w				contrato.cd_contrato%type;
ds_objeto_contrato_w		contrato.ds_objeto_contrato%type;
nm_usuario_w				comunic_interna.nm_usuario_destino%type;
nm_contratado_w				varchar(80);
nr_seq_contrato_w			contrato.nr_sequencia%type;
nm_usuario_lib_w				varchar(15);
ds_regra_w					contrato_regra_doc.ds_regra%type;
ds_titulo_contrato_w		contrato.ds_titulo_contrato%type;
cd_estabelecimento_w			smallint;

C01 CURSOR FOR
SELECT	substr(a.ds_titulo_contrato,1,255),
	a.cd_pessoa_resp,
	a.cd_contrato,
	a.ds_objeto_contrato,
	substr(obter_razao_social(a.cd_cgc_contratado),1,80),
	a.nr_sequencia,
	b.ds_regra
from	contrato a,
	contrato_regra_doc b
where	a.nr_sequencia = b.nr_seq_contrato
and	ie_situacao = 'A'
and	cd_estabelecimento = cd_estabelecimento_w
and	coalesce(b.ie_avisa_vencimento,'N') = 'S'
and	coalesce(a.ie_finalizado,'N') = 'N'
and	(((b.dt_especifica IS NOT NULL AND b.dt_especifica::text <> '') and to_date(b.dt_especifica) = to_date(clock_timestamp())) or (to_char(b.qt_dia_mes) = to_char(clock_timestamp(),'dd')))
and not exists (
	SELECT	1
	from	contrato_controle_doc x
	where	x.nr_seq_regra = b.nr_sequencia);

c02 CURSOR FOR
SELECT	distinct
	coalesce(nm_usuario_lib,'X')
from	contrato_usuario_lib
where	nr_seq_contrato = nr_seq_contrato_w
and	coalesce(ie_aviso_vencimento,'N') = 'S';

c03 CURSOR FOR

SELECT	cd_estabelecimento
from 	estabelecimento
where	ie_situacao = 'A'
and 	cd_estabelecimento_p = 0

union all

SELECT	cd_estabelecimento_p

where	cd_estabelecimento_p > 0;



BEGIN

open C03;
loop
fetch C03 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin

	open C01;
	loop
	fetch C01 into
		ds_titulo_contrato_w,
		cd_pessoa_responsavel_w,
		cd_contrato_w,
		ds_objeto_contrato_w,
		nm_contratado_w,
		nr_seq_contrato_w,
		ds_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	coalesce(max(nm_usuario),'X') || ', '
		into STRICT	nm_usuario_w
		from	usuario
		where	cd_pessoa_fisica = cd_pessoa_responsavel_w;

		open c02;
		loop
		fetch c02 into
			nm_usuario_lib_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */

			if (nm_usuario_lib_w <> 'X') then
				nm_usuario_w := nm_usuario_w || nm_usuario_lib_w || ', ';
			end if;
		end loop;
		close c02;

		if (nm_usuario_w <> 'X') then
			begin
			select	nextval('comunic_interna_seq')
			into STRICT	nr_sequencia_w
			;

			select	obter_classif_comunic('F')
			into STRICT	nr_seq_classif_w
			;

			insert into comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				dt_atualizacao,
				ie_geral,
				nm_usuario_destino,
				nr_sequencia,
				ie_gerencial,
				nr_seq_classif,
				dt_liberacao)
			values (clock_timestamp() + interval '1 days'/86400,
				wheb_mensagem_pck.get_texto(309971),
				wheb_mensagem_pck.get_texto(309942) || ': ' || nr_seq_Contrato_w || chr(13) || ' ' ||
				wheb_mensagem_pck.get_texto(309943) || ': ' || ds_titulo_contrato_w || chr(13) || ' ' ||
				wheb_mensagem_pck.get_texto(301996) || ': ' || cd_contrato_w || ' - ' || ds_objeto_contrato_w || chr(13) || ' ' ||
				wheb_mensagem_pck.get_texto(309944) || ': ' || nm_contratado_w || chr(13) || ' ' ||
				wheb_mensagem_pck.get_texto(309991) || ': ' || ds_regra_w,
				'Aviso_Tasy',
				clock_timestamp(),
				'N',
				nm_usuario_w || ',',
				nr_sequencia_w,
				'N',
				nr_seq_classif_w,
				clock_timestamp());
			end;
		end if;
		end;
	end loop;
	close c01;

	end;
end loop;
close C03;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
