SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE avisar_venc_proj_rec () AS $body$
DECLARE


nr_seq_proj_w		bigint;
ds_texto_ci_w		varchar(4000);
ds_titulo_w		varchar(255);
nm_usuario_dest_w	varchar(15);
cd_pf_representante_w	bigint;

C01 CURSOR FOR
/*Aviso de fim de execução do projeto recurso*/

SELECT	nr_sequencia,
	cd_pf_representante,
	wheb_mensagem_pck.get_texto(310023,'DS_PROJETO='||ds_projeto||';DT_FIM='||obter_dias_entre_datas(clock_timestamp(),dt_fim_exec)) ds_texto,
	wheb_mensagem_pck.get_texto(310030) ds_titulo
from	projeto_recurso
where	(cd_pf_representante IS NOT NULL AND cd_pf_representante::text <> '')
and	(qt_dias_fim_exec IS NOT NULL AND qt_dias_fim_exec::text <> '')
and	(dt_fim_exec IS NOT NULL AND dt_fim_exec::text <> '')
and	trunc(clock_timestamp(),'dd') between trunc(dt_fim_exec - qt_dias_fim_exec,'dd') and trunc(dt_fim_exec,'dd')

union all

/*Aviso de fim de prestação de compras do projeto recurso*/

select	nr_sequencia,
	cd_pf_representante,
	wheb_mensagem_pck.get_texto(310036,'DS_PROJETO='||ds_projeto||';DT_FIM='||obter_dias_entre_datas(clock_timestamp(),dt_fim_prest_conta)) ds_texto,
	wheb_mensagem_pck.get_texto(310039) ds_titulo
from	projeto_recurso
where	(cd_pf_representante IS NOT NULL AND cd_pf_representante::text <> '')
and	(qt_dias_fim_prest IS NOT NULL AND qt_dias_fim_prest::text <> '')
and	(dt_fim_prest_conta IS NOT NULL AND dt_fim_prest_conta::text <> '')
and	trunc(clock_timestamp(),'dd') between trunc(dt_fim_prest_conta - qt_dias_fim_prest,'dd') and trunc(dt_fim_prest_conta,'dd')

union all

/*Aviso de fim de execução do valor do projeto*/

select	a.nr_sequencia,
	a.cd_pf_representante,
	wheb_mensagem_pck.get_texto(310045,'NR_PROCESSO='||substr(a.nr_processo,1,60)||';DS_PROJETO='||DS_PROJETO||';DT_FIM='||obter_dias_entre_datas(clock_timestamp(),b.dt_fim_exec)) ds_texto,
	wheb_mensagem_pck.get_texto(310046) ds_titulo
from	projeto_recurso a,
	projeto_recurso_vigencia b
where	a.nr_sequencia = b.nr_seq_projeto
and	(a.cd_pf_representante IS NOT NULL AND a.cd_pf_representante::text <> '')
and	(b.qt_dias_fim_exec IS NOT NULL AND b.qt_dias_fim_exec::text <> '')
and	(b.dt_fim_exec IS NOT NULL AND b.dt_fim_exec::text <> '')
and	trunc(clock_timestamp(),'dd') between trunc(b.dt_fim_exec - b.qt_dias_fim_exec,'dd') and trunc(b.dt_fim_exec,'dd')

union all

/*Aviso de fim de prestação de contas do valor projeto*/

select	a.nr_sequencia,
	a.cd_pf_representante,
	wheb_mensagem_pck.get_texto(310054,'NR_PROCESSO='||substr(a.nr_processo,1,60)||';DS_PROJETO='||DS_PROJETO||';DT_FIM='||obter_dias_entre_datas(clock_timestamp(),b.dt_fim_prest_conta)) ds_texto,
	wheb_mensagem_pck.get_texto(310055) ds_titulo
from	projeto_recurso a,
	projeto_recurso_vigencia b
where	a.nr_sequencia = b.nr_seq_projeto
and	(a.cd_pf_representante IS NOT NULL AND a.cd_pf_representante::text <> '')
and	(b.qt_dias_fim_prest IS NOT NULL AND b.qt_dias_fim_prest::text <> '')
and	(b.dt_fim_prest_conta IS NOT NULL AND b.dt_fim_prest_conta::text <> '')
and	trunc(clock_timestamp(),'dd') between trunc(b.dt_fim_prest_conta - b.qt_dias_fim_prest,'dd') and trunc(b.dt_fim_prest_conta,'dd');


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_proj_w,
	cd_pf_representante_w,
	ds_texto_ci_w,
	ds_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nm_usuario)
	into STRICT	nm_usuario_dest_w
	from	usuario
	where	cd_pessoa_fisica = cd_pf_representante_w;

	if (nm_usuario_dest_w IS NOT NULL AND nm_usuario_dest_w::text <> '') then

		insert	into comunic_interna(
			nr_sequencia,
			dt_comunicado,
			ds_titulo,
			ds_comunicado,
			nm_usuario,
			dt_atualizacao,
			ie_geral,
			ie_gerencial,
			dt_liberacao,
			nm_usuario_destino)
		values (
			nextval('comunic_interna_seq'),
			clock_timestamp(),
			ds_titulo_w,
			ds_texto_ci_w,
			'Job',
			clock_timestamp(),
			'N',
			'N',
			clock_timestamp(),
			nm_usuario_dest_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE avisar_venc_proj_rec () FROM PUBLIC;

