CREATE OR REPLACE PROCEDURE avisa_medico_referido (cd_medico_referido_p text, ie_forma_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ie_corpo_clinico_w		varchar(1);


BEGIN

if (cd_medico_referido_p IS NOT NULL AND cd_medico_referido_p::text <> '') then

	select	coalesce(max(ie_corpo_clinico),'')
	into STRICT	ie_corpo_clinico_w
	from	medico
	where	cd_pessoa_fisica	= cd_medico_referido_p;

	if	(ie_forma_p = 'S' AND ie_corpo_clinico_w = 'S') or (ie_forma_p = 'T') then
		ds_retorno_p	:= SUBSTR(obter_texto_tasy(292853, wheb_usuario_pck.get_nr_seq_idioma),1,255);
	end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
