SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acertar_nut_acomp ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_servico_w		bigint;
nr_seq_acompanhante_w		bigint;
qt_reg_acompanhante_w		bigint;
qt_tabela_acomp_w		bigint;
qt_tabela_dieta_w		bigint;
nr_seq_nut_atend_acomp_w	bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	nut_atend_serv_dia
	where	trunc(dt_atualizacao_nrec) >= to_date('28/10/2011','dd/mm/yyyy');

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	nut_atend_acompanhante
	where	nr_seq_atend_serv_dia = nr_seq_servico_w
	and	nr_sequencia <> nr_seq_acompanhante_w;


BEGIN

select	count(*)
into STRICT	qt_tabela_dieta_w
from	user_tables
where	table_name = 'BKP_NUT_ATEND_ACOMP_DIETA';

if (qt_tabela_dieta_w = 0) then

	CALL exec_sql_dinamico('Tasy','create table BKP_NUT_ATEND_ACOMP_DIETA as select * from NUT_ATEND_ACOMP_DIETA');

end if;

select	count(*)
into STRICT	qt_tabela_acomp_w
from	user_tables
where	table_name = 'BKP_NUT_ATEND_ACOMPANHANTE';

if (qt_tabela_acomp_w = 0) then

	CALL exec_sql_dinamico('Tasy','create table BKP_NUT_ATEND_ACOMPANHANTE as select * from NUT_ATEND_ACOMPANHANTE');

end if;


open C01;
loop
fetch C01 into
	nr_seq_servico_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_reg_acompanhante_w
	from	nut_atend_acompanhante
	where	nr_seq_atend_serv_dia = nr_seq_servico_w;

	if (qt_reg_acompanhante_w > 1) then

		select	max(nr_sequencia)
		into STRICT	nr_seq_acompanhante_w
		from	nut_atend_acompanhante
		where	nr_seq_atend_serv_dia = nr_seq_servico_w;

		open C02;
		loop
		fetch C02 into
			nr_seq_nut_atend_acomp_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			delete	from nut_atend_acomp_dieta
			where	nr_seq_atend_acomp = nr_seq_nut_atend_acomp_w;

			delete	from nut_atend_acompanhante
			where	nr_sequencia = nr_seq_nut_atend_acomp_w;

			end;
		end loop;
		close C02;

		commit;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
