CREATE OR REPLACE PROCEDURE baca_acertar_percent_proj_migr (nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
nr_seq_cronograma_w	bigint;

c01 CURSOR FOR
SELECT	p.nr_sequencia,
	c.nr_sequencia
from	proj_cronograma c,
	proj_projeto p
where	c.nr_seq_proj = p.nr_sequencia
and	(p.cd_funcao IS NOT NULL AND p.cd_funcao::text <> '')
and	p.nr_seq_classif = 14
and	p.nr_seq_gerencia = 3
order by
	p.nr_sequencia,
	c.nr_sequencia;


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into	nr_seq_projeto_w,
			nr_seq_cronograma_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_w, nm_usuario_p);
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
$body$
