CREATE OR REPLACE PROCEDURE baca_acertar_sinal_vital_pac () AS $body$
DECLARE

 
qt_existe_w	bigint;


BEGIN 
select count(*) 
into STRICT	qt_existe_w 
from	atendimento_sinal_vital 
where	coalesce(cd_paciente::text, '') = '' 
and		(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');
 
if (qt_existe_w > 0) then 
	begin 
	update	atendimento_sinal_vital 
	set		cd_paciente = obter_pessoa_atendimento(nr_atendimento,'C') 
	where 	coalesce(cd_paciente::text, '') = '' 
	and		(nr_atendimento IS NOT NULL AND nr_atendimento::text <> '');
	end;
end if;
commit;
end;
