SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_banco_saldo () AS $body$
DECLARE


dt_transaco_w		timestamp;
retorno_w		integer;
nr_sequencia_w		bigint;
nr_seq_banco_w		bigint;
nr_seq_saldo_banco_w	bigint;


c01 CURSOR FOR
SELECT	dt_transacao,
	nr_sequencia,
	nr_seq_banco
from	movto_trans_financ
where	(nr_seq_banco IS NOT NULL AND nr_seq_banco::text <> '')
and	nr_seq_saldo_banco not in (select	nr_sequencia
		from	banco_saldo);


BEGIN

retorno_w := Obter_Valor_Dinamico('ALTER TABLE MOVTO_TRANS_FINANC drop constraint MOVTRFI_BANSALD_FK', retorno_w);

open c01;
loop
fetch c01 into
	dt_transaco_w,
	nr_sequencia_w,
	nr_seq_banco_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	nr_sequencia
	into STRICT	nr_seq_saldo_banco_w
	from	banco_saldo
	where	trunc(dt_referencia, 'month')	= trunc(dt_transaco_w, 'month')
	and	nr_seq_conta			= nr_seq_banco_w;

	update	movto_trans_financ
	set	nr_seq_saldo_banco	= nr_seq_saldo_banco_w
	where	nr_sequencia		= nr_sequencia_w;

end loop;
close c01;

commit;

retorno_w := Obter_Valor_Dinamico(	'ALTER TABLE MOVTO_TRANS_FINANC ADD (CONSTRAINT MOVTRFI_BANSALD_FK FOREIGN KEY ( ' ||
			' NR_SEQ_SALDO_BANCO) REFERENCES BANCO_SALDO (NR_SEQUENCIA))', retorno_w);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
