CREATE OR REPLACE PROCEDURE baca_acerto_bloq_agenda () AS $body$
DECLARE


QT_REG_W	bigint;


BEGIN

update	agenda_bloqueio
set	nr_sequencia	= nextval('agenda_bloqueio_seq')
where	coalesce(nr_sequencia::text, '') = '';

select	count(*)
into STRICT	qt_reg_w
from 	user_cons_columns
where 	CONSTRAINT_NAME = 'AGEBLOQ_PK';

if (qt_reg_w > 1) then
	begin

	CALL Exec_sql_Dinamico('Agenda Bloqueio', ' alter table agenda_bloqueio modify (nr_sequencia number(10,0) not null) ');

	CALL Exec_sql_Dinamico('Agenda Bloqueio', ' ALTER TABLE agenda_bloqueio drop CONSTRAINT AGEBLOQ_PK ');

	CALL Exec_sql_Dinamico('Agenda Bloqueio', ' ALTER TABLE agenda_bloqueio ADD ( CONSTRAINT AGEBLOQ_PK Primary Key  (
				               nr_sequencia)) ');

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
