CREATE OR REPLACE PROCEDURE baca_acerto_classif_pro_rata (nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w	bigint;
nr_sequencia_w	bigint;

c01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.nr_sequencia 
from	titulo_receber_liq a 
where	(a.dt_antecipacao_pls_mens IS NOT NULL AND a.dt_antecipacao_pls_mens::text <> '');


BEGIN 
 
open c01;
loop 
fetch c01 into	 
	nr_titulo_w, 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	update	titulo_receber_liq 
	set	dt_antecipacao_pls_mens	 = NULL 
	where	nr_titulo	= nr_titulo_w 
	and	nr_sequencia	= nr_sequencia_w;
	 
	CALL recalcular_titulo_rec_liq_cc(nr_titulo_w,nr_sequencia_w,nm_usuario_p);
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
