SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_desc_tit_classif (dt_parametro_p timestamp) AS $body$
DECLARE

 
nr_titulo_w	bigint;
qt_titulo_w	bigint := 0;

c01 CURSOR FOR 
SELECT	distinct b.nr_titulo 
from	titulo_pagar_classif b, 
	titulo_pagar_baixa a 
where	a.nr_titulo		= b.nr_titulo 
and	a.VL_DESCONTOS		> 0 
and	a.dt_atualizacao	> dt_parametro_p 
and	coalesce(b.vl_desconto,0)	= 0;

c02 CURSOR FOR 
SELECT	distinct b.nr_titulo 
from	titulo_receber_classif b, 
	titulo_receber_liq a 
where	a.nr_titulo		= b.nr_titulo 
and	a.VL_DESCONTOS		> 0 
and	a.dt_atualizacao	> dt_parametro_p 
and	coalesce(b.vl_desconto,0)	= 0;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL atualizar_tit_pagar_classif(nr_titulo_w, 'N', null);
	qt_titulo_w	:= qt_titulo_w + 1;
end loop;
close c01;
 
RAISE NOTICE 'Qt TP: %', qt_titulo_w;
 
qt_titulo_w	:= 0;
open c02;
loop 
fetch c02 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c02 */
	CALL atualizar_tit_receber_classif(nr_titulo_w, 'N', null);
	qt_titulo_w	:= qt_titulo_w + 1;
end loop;
close c02;
 
RAISE NOTICE 'Qt TR: %', qt_titulo_w;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
