SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_doc_tit_rec (nr_seq_retorno_p bigint) AS $body$
DECLARE


cont_guia_w		bigint;
cont_titulo_w		bigint;
nr_documento_w		varchar(255);
nr_interno_conta_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_interno_conta,
	a.cd_autorizacao
from	convenio_retorno_item a
where	a.nr_seq_retorno	= nr_seq_retorno_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_interno_conta_w,
	nr_documento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	count(*)
	into STRICT	cont_guia_w
	from	conta_paciente_guia
	where	nr_interno_conta	= nr_interno_conta_w;

	select	count(*)
	into STRICT	cont_titulo_w
	from	titulo_receber
	where	nr_interno_conta	= nr_interno_conta_w;

	if (cont_guia_w = 1) and (cont_titulo_w = 1) then

		update	titulo_receber
		set	nr_documento		= nr_documento_w
		where	nr_interno_conta	= nr_interno_conta_w;

	end if;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_acerto_doc_tit_rec (nr_seq_retorno_p bigint) FROM PUBLIC;

