CREATE OR REPLACE PROCEDURE baca_acerto_duplic_agenda () AS $body$
DECLARE


dt_agenda_w		timestamp;
cd_pessoa_fisica_w	varchar(10);
cd_agenda_w		bigint;
ie_classif_agenda_w	varchar(05);
nr_seq_agenda_w		bigint;

c01 CURSOR FOR
	SELECT	cd_agenda,
		dt_agenda,
		cd_pessoa_fisica,
		ie_classif_agenda
	from 	agenda_consulta a
	where	upper(nm_usuario) = 'IMPORT'
	group by cd_agenda,
		dt_agenda,
		cd_pessoa_fisica,
		ie_classif_agenda
	having count(*) > 1;


BEGIN

open	c01;
loop
fetch	c01 into
	cd_agenda_w,
	dt_agenda_w,
	cd_pessoa_fisica_w,
	ie_classif_agenda_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_agenda_w
	from	agenda_consulta
	where	cd_agenda		= cd_agenda_w
	and	dt_agenda		= dt_agenda_w
	and	ie_classif_agenda	= ie_classif_agenda_w
	and	cd_pessoa_fisica	= cd_pessoa_fisica_w;

	if (nr_seq_agenda_w > 0) then
		begin

		delete from agenda_consulta
		where	cd_agenda		= cd_agenda_w
		and	dt_agenda		= dt_agenda_w
		and	ie_classif_agenda	= ie_classif_agenda_w
		and	cd_pessoa_fisica	= cd_pessoa_fisica_w
		and	nr_sequencia		<> nr_seq_agenda_w;

		end;
	end if;
	end;
end loop;
close	c01;

commit;

end;
