CREATE OR REPLACE PROCEDURE baca_acerto_lib_cheque_cr () AS $body$
DECLARE


qt_registro_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	cheque_cr
where	coalesce(ie_lib_caixa::text, '') = '';

while(qt_registro_w	> 0) loop

	update	cheque_cr a
	set	ie_lib_caixa	= 'S'
	where	coalesce(ie_lib_caixa::text, '') = ''  LIMIT 5000;

	commit;

	qt_registro_w	:= qt_registro_w - 5000;
end loop;

END;
