CREATE OR REPLACE PROCEDURE baca_acerto_material_lab () AS $body$
DECLARE


nr_prescricao_w 	bigint;
nr_seq_exame_w		bigint;
ie_prioridade_w 	smallint;
cd_material_exame_w	varchar(20);
nr_sequencia_w		integer;

C01 CURSOR FOR

SELECT 	c.nr_prescricao,
	c.nr_seq_exame,
	c.nr_sequencia
from 	prescr_procedimento c
where  	c.cd_material_exame not in (SELECT a.cd_material_exame
				 from material_exame_lab a,
				 exame_lab_material b
				 where 	c.nr_seq_exame = b.nr_seq_exame
				 and 	a.nr_sequencia = b.nr_seq_material)
and	(c.nr_seq_exame IS NOT NULL AND c.nr_seq_exame::text <> '');


BEGIN

OPEN C01;
LOOP
FETCH C01 into	nr_prescricao_w,
		nr_seq_exame_w,
		nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	begin

	select	min(ie_prioridade)
	into STRICT	ie_prioridade_w
	from 	exame_lab_material
	where	nr_seq_exame = nr_seq_exame_w;

	select 	max(a.cd_material_exame)
	into STRICT	cd_material_exame_w
	from 	material_exame_lab a,
		exame_lab_material b
	where 	a.nr_sequencia 	= b.nr_seq_material
	and	b.ie_prioridade = ie_prioridade_w
	and	b.nr_seq_exame 	= nr_seq_exame_w;

	update	prescr_procedimento
	set	cd_material_exame = cd_material_exame_w
	where	nr_sequencia	= nr_sequencia_w
	and	nr_prescricao	= nr_prescricao_w;
	exception
     		when others then
			ie_prioridade_w := 999;

	end;

end loop;
close c01;

commit;

end;
