CREATE OR REPLACE PROCEDURE baca_acerto_movto_baixa_cartao () AS $body$
DECLARE


nr_seq_movto_w	bigint;
dt_baixa_w	timestamp;

c01 CURSOR FOR
SELECT	distinct b.nr_sequencia
from	movto_cartao_cr b,
	movto_cartao_cr_baixa a
where	a.nr_seq_movto	= b.nr_sequencia
and	a.dt_baixa	< b.dt_transacao
order by 1;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_movto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	select	min(dt_baixa)
	into STRICT	dt_baixa_w
	from	movto_cartao_cr_baixa
	where	nr_seq_movto	= nr_seq_movto_w;

	update	movto_cartao_cr
	set	dt_transacao	= dt_baixa_w
	where	nr_sequencia	= nr_seq_movto_w;
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
