CREATE OR REPLACE PROCEDURE baca_acerto_oficializacao () AS $body$
DECLARE


nr_seq_ofic_w		bigint;
nr_seq_proj_w		bigint;
nr_seq_cliente_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_proj
	from	PROJ_OFICIALIZACAO_USO
	where	coalesce(nr_seq_cliente::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ofic_w,
	nr_seq_proj_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_seq_cliente)
	into STRICT	nr_seq_cliente_w
	from	proj_projeto
	where	nr_sequencia	= nr_seq_proj_w;

	update	PROJ_OFICIALIZACAO_USO
	set	nr_seq_cliente	= nr_seq_cliente_w
	where	nr_sequencia	= nr_seq_ofic_w;

	end;
end loop;
close C01;

commit;

end;
