SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_ordem_atend () AS $body$
DECLARE

 
nr_ordem_compra_w	bigint;
ds_observacao_w		varchar(4000);
nr_atendimento_w	bigint;

c01 CURSOR FOR 
	SELECT	nr_ordem_compra, 
		ds_observacao 
	from	ordem_compra 
	where	upper(ds_observacao) like '%ATENDIMENTO:%';


BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_ordem_compra_w, 
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	select	(substr(ds_observacao, position('ATENDIMENTO' in upper(ds_observacao)) + 13, 
		position(' ' in upper(substr(ds_observacao, position('ATENDIMENTO' in upper(ds_observacao)) + 13, 10))) - 1))::numeric  
	into STRICT	nr_atendimento_w 
	from	ordem_compra 
	where	nr_ordem_compra = nr_ordem_compra_w;
 
 
	select	coalesce(max(nr_atendimento),0) 
	into STRICT	nr_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_w;
 
 
	if (nr_atendimento_w > 0) then 
		update	ordem_compra 
		set	nr_atendimento	= nr_atendimento_w 
		where	nr_ordem_compra = nr_ordem_compra_w 
		and	coalesce(nr_atendimento::text, '') = '';
	end if;	
 
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
