SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_pessoa_fisica () AS $body$
DECLARE


nr_identidade_w		varchar(20);
nr_ident_nova_w		varchar(20);
cd_pessoa_fisica_w	varchar(20);
nm_pessoa_fisica_w	varchar(254);
qt_registro_w		bigint;

c01 CURSOR FOR
SELECT	nm_pessoa_fisica,
	nr_identidade,
	cd_pessoa_fisica
from	pessoa_fisica;


BEGIN

qt_registro_w := Obter_Valor_Dinamico('drop table PESSOA_FISICA_BKP ', qt_registro_w);


qt_registro_w := Obter_Valor_Dinamico('create table PESSOA_FISICA_BKP as select * from pessoa_fisica', qt_registro_w);

open c01;
loop
fetch c01 into
	nm_pessoa_fisica_w,
	nr_identidade_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

nm_pessoa_fisica_w		:= trim(both nm_pessoa_fisica_w);
while(position('  ' in nm_pessoa_fisica_w) > 0) loop
	nm_pessoa_fisica_w	:= replace(nm_pessoa_fisica_w, '  ', ' ');
end loop;

nr_ident_nova_w		:= '';
nr_identidade_w		:= upper(nr_identidade_w);
for	i in 1..coalesce(length(nr_identidade_w),0) loop		-- validar identidade, somente letras e nÃºmeros
	if (position(substr(nr_identidade_w, i, 1) in '0123456789ABCDEFGHIJKLMNOPQRSTUVXWYV') > 0) then
		nr_ident_nova_w		:= nr_ident_nova_w || substr(nr_identidade_w, i, 1);
	end if;

end loop;

nr_identidade_w		:= nr_ident_nova_w;

if (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') then
	update	pessoa_fisica
	set	nm_pessoa_fisica	= nm_pessoa_fisica_w,
		nr_identidade		= nr_ident_nova_w
	where	cd_pessoa_fisica	= cd_pessoa_fisica_w;
end if;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
