SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_portador_pj_estab (nm_usuario_p text) AS $body$
DECLARE


cd_cgc_w		varchar(14);
cd_estabelecimento_w	smallint;
cd_tipo_portador_w	integer;
cd_portador_w		bigint;
nr_sequencia_w		bigint;
cont_w			bigint;

c01 CURSOR FOR
SELECT	cd_cgc,
	cd_tipo_portador,
	cd_portador
from	pessoa_juridica
where ((cd_tipo_portador IS NOT NULL AND cd_tipo_portador::text <> '') or
	(cd_portador IS NOT NULL AND cd_portador::text <> ''));

c02 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	ie_situacao = 'A';


BEGIN

select	count(*)
into STRICT	cont_w
from	pessoa_juridica_estab
where ((cd_tipo_portador IS NOT NULL AND cd_tipo_portador::text <> '') or
	(cd_portador IS NOT NULL AND cd_portador::text <> ''));

if (cont_w = 0) then

	open c01;
	loop
	fetch c01 into
		cd_cgc_w,
		cd_tipo_portador_w,
		cd_portador_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		open c02;
		loop
		fetch c02 into
			cd_estabelecimento_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			select	coalesce(max(nr_sequencia),0)
			into STRICT	nr_sequencia_w
			from	pessoa_juridica_estab
			where	cd_cgc			= cd_cgc_w
			and	cd_estabelecimento	= cd_estabelecimento_w;

			if (nr_sequencia_w = 0) then
				insert	into	pessoa_juridica_estab(nr_sequencia,
					nm_usuario_nrec,
					dt_atualizacao_nrec,
					nm_usuario,
					dt_atualizacao,
					cd_cgc,
					cd_estabelecimento,
					cd_tipo_portador,
					cd_portador,
					ie_conta_dif_nf)
				values (nextval('pessoa_juridica_estab_seq'),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					cd_cgc_w,
					cd_estabelecimento_w,
					cd_tipo_portador_w,
					cd_portador_w,
					'N');
			else
				update	pessoa_juridica_estab
				set	cd_tipo_portador	= cd_tipo_portador_w,
					cd_portador		= cd_portador_w,
					nm_usuario		= nm_usuario_p,
					dt_atualizacao		= clock_timestamp()
				where	nr_sequencia		= nr_sequencia_w;
			end if;
		end loop;
		close c02;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
