SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_prod_financ () AS $body$
DECLARE


cd_estabelecimento_w	bigint;
cont_w			bigint;
nr_sequencia_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	produto_financeiro
where	coalesce(nr_seq_grupo::text, '') = '';

if (cont_w > 0) then

	select	min(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	produto_financeiro
	where	coalesce(nr_seq_grupo::text, '') = '';

	select	nextval('grupo_prod_financ_seq')
	into STRICT	nr_sequencia_w
	;

	insert into grupo_prod_financ(NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		DS_GRUPO,
		CD_ESTABELECIMENTO)
	values (nr_sequencia_w,
		clock_timestamp(),
		'Tasy',
		clock_timestamp(),
		'Tasy',
		'Sem grupo definido',
		cd_estabelecimento_w);

	update	produto_financeiro
	set	nr_seq_grupo	= nr_sequencia_w
	where	coalesce(nr_seq_grupo::text, '') = '';

	commit;
end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_acerto_prod_financ () FROM PUBLIC;

