SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_saida_unidade () AS $body$
DECLARE


rowid_w		oid;
qt_reg_w		bigint;
qt_tot_w		bigint;

C01 CURSOR FOR
	SELECT	oid
	from	atend_paciente_unidade
	where	(dt_saida_unidade IS NOT NULL AND dt_saida_unidade::text <> '')
	and	dt_saida_unidade	<> dt_saida_interno;


BEGIN

qt_reg_w		:= 0;
qt_tot_w		:= 0;
OPEN C01;
LOOP
FETCH C01 into	rowid_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	atend_paciente_unidade
	set	dt_saida_interno	= dt_saida_unidade
	where rowid = rowid_w;
	qt_reg_w	:= qt_reg_w + 1;
	exception
		when others then
			qt_reg_w	:= qt_reg_w;
	end;
	if (qt_reg_w > 5000) then
		qt_tot_w	:= qt_tot_w + qt_reg_w;
		/*insert into logxxxx_tasy values(sysdate, 'Tasy', 981, 'Registros atualizados: ' || qt_tot_w);
		commit;*/
		qt_reg_w	:= 0;
	end if;
END LOOP;
close c01;

qt_tot_w	:= qt_tot_w + qt_reg_w;
/*insert into logxxxx_tasy values(sysdate, 'Tasy', 981, 'Registros atualizados: ' || qt_tot_w);*/

commit;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
