CREATE OR REPLACE PROCEDURE baca_acerto_saldo_tit_pagar () AS $body$
DECLARE

 
nr_titulo_w		bigint;
vl_saldo_titulo_w	double precision;
vl_bordero_w		double precision;
nr_bordero_w		bigint;

c01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.nr_bordero, 
	a.vl_bordero, 
	a.vl_saldo_titulo 
from	titulo_pagar_imposto b, 
	titulo_pagar a	 
where	a.nr_titulo	= b.nr_titulo 
and	b.ie_pago_prev	= 'V' 
and	a.ie_situacao	= 'A' 
and	a.vl_saldo_titulo	> 0;


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w, 
	nr_bordero_w, 
	vl_bordero_w, 
	vl_saldo_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	begin 
	CALL atualizar_saldo_tit_pagar(nr_titulo_w, 'Tasy');
 
	if (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '') and (vl_bordero_w = vl_saldo_titulo_w) then 
 
		select	vl_saldo_titulo 
		into STRICT	vl_saldo_titulo_w 
		from	titulo_pagar 
		where	nr_titulo	= nr_titulo_w;
 
		update	titulo_pagar 
		set	vl_bordero	= vl_saldo_titulo_w 
		where	nr_titulo	= nr_titulo_w;
 
	end if;
	RAISE NOTICE 'Foi: %', nr_titulo_w;
	exception 
		when others then 
			RAISE NOTICE 'NÃ£o foi: %', nr_titulo_w;
	end;
 
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
