SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_tit_pagar_classif (dt_inicial_p timestamp) AS $body$
DECLARE



nr_titulo_w		bigint;
vl_titulo_w		double precision;
vl_base_classif_w	double precision;
vl_total_classif_w	double precision;

c01 CURSOR FOR
SELECT	distinct a.nr_titulo,
	a.vl_titulo
from	titulo_pagar_classif b,
	titulo_pagar a
where	a.nr_titulo		= b.nr_titulo
and	a.dt_atualizacao	>= trunc(dt_inicial_p, 'dd');

c02 CURSOR FOR
SELECT	vl_alteracao
from	titulo_pagar_alt_valor
where	nr_titulo		= nr_titulo_w
order	by nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w,
	vl_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	vl_base_classif_w		:= vl_titulo_w;
	open c02;
	loop
	fetch c02 into
		vl_base_classif_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
	end loop;
	close c02;

	select	sum(vl_titulo)
	into STRICT	vl_total_classif_w
	from	titulo_pagar_classif
	where	nr_titulo	= nr_titulo_w;

	if (vl_total_classif_w <> vl_base_classif_w) then
		CALL ATUALIZAR_TIT_PAGAR_CLASSIF(nr_titulo_w, 'N', 'TASY');
		RAISE NOTICE 'nr_titulo_w = %', nr_titulo_w;
	end if;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
