SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_tit_protocolo () AS $body$
DECLARE


nr_titulo_w		bigint;
cd_cgc_atual_w		varchar(255);
cd_cgc_anterior_w	varchar(255);

c01 CURSOR FOR
SELECT	b.nr_titulo,
	c.cd_cgc,
	b.cd_cgc
from	convenio c,
	titulo_receber b,
	protocolo_convenio a
where	a.nr_seq_protocolo	= b.nr_seq_protocolo
and	a.cd_convenio		= c.cd_convenio
and	(b.cd_cgc IS NOT NULL AND b.cd_cgc::text <> '')
and	b.cd_cgc		<> c.cd_cgc
and	b.dt_emissao		> to_date('01/01/2009', 'dd/mm/yyyy');


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w,
	cd_cgc_atual_w,
	cd_cgc_anterior_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into TIT_REC_ALT_PESSOA(NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_TITULO,
		DT_ALTERACAO,
		CD_PESSOA_FISICA_ANT,
		CD_PESSOA_FISICA,
		CD_CGC_ANT,
		CD_CGC)
	SELECT	nextval('tit_rec_alt_pessoa_seq'),
		clock_timestamp(),
		'TASY',
		clock_timestamp(),
		'TASY',
		nr_titulo_w,
		clock_timestamp(),
		null,
		null,
		cd_cgc_anterior_w,
		cd_cgc_atual_w
	;

	update	titulo_receber
	set	cd_cgc		= cd_cgc_atual_w
	where	nr_titulo	= nr_titulo_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
