SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_unid_dialisador () AS $body$
DECLARE



nr_seq_dialisador_w		bigint;
qt_registro_w			bigint;
nr_seq_unidade_w		bigint;
nr_seq_transf_w			bigint;




C01 CURSOR FOR
	SELECT	nr_sequencia
	from	hd_dializador a
	where	coalesce(a.nr_seq_unid_dialise_atual::text, '') = ''
	order by 1;


BEGIN

CALL exec_sql_dinamico('OS373709', 'alter trigger ' || 'HD_DIALIZADOR_BEFORE_UPDATE' || ' disable');


open C01;
loop
fetch C01 into
	nr_seq_dialisador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	hd_dialisador_transf
	where	nr_seq_dialisador	= nr_seq_dialisador_w;

	if (qt_registro_w > 0) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_transf_w
		from	hd_dialisador_transf
		where	nr_seq_dialisador	= nr_seq_dialisador_w;

		select	nr_seq_unid_destino
		into STRICT	nr_seq_unidade_w
		from	hd_dialisador_transf
		where	nr_sequencia		= nr_seq_transf_w;
	else
		select	nr_seq_unid_dialise
		into STRICT	nr_seq_unidade_w
		from	hd_dializador
		where	nr_sequencia		= nr_seq_dialisador_w;
	end if;


	update	hd_dializador
	set	nr_seq_unid_dialise_atual = nr_seq_unidade_w
	where	nr_sequencia = nr_seq_dialisador_w;


	end;
end loop;
close C01;

CALL exec_sql_dinamico('OS373709', 'alter trigger ' || 'HD_DIALIZADOR_BEFORE_UPDATE' || ' enable');
commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
