CREATE OR REPLACE PROCEDURE baca_acerto_vacina_calendario () AS $body$
DECLARE


nr_seq_vacina_w	bigint;
ie_dose_w		varchar(03);

c01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_dose
	from	vacina_calendario
	where	coalesce(nr_seq_vacina::text, '') = '';


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_vacina_w,
	ie_dose_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	update	vacina_calendario
	set	nr_seq_vacina	= nr_seq_vacina_w
	where	nr_sequencia	= nr_seq_vacina_w
	and	ie_dose	= ie_dose_w;

	update	vacina_calendario
	set	nr_sequencia	= nextval('vacina_calendario_seq')
	where	nr_sequencia	= nr_seq_vacina_w
	and	ie_dose	= ie_dose_w;

	end;
end loop;
close c01;

commit;

end;
$body$
