CREATE OR REPLACE PROCEDURE baca_acerto_vinc_profissional () AS $body$
DECLARE


ie_vinculo_medico_w	smallint;
cd_pessoa_fisica_w	varchar(10);

c01 CURSOR FOR
SELECT	a.ie_vinculo_medico,
	a.cd_pessoa_fisica
from	medico a
where	exists (select	1
		from	pessoa_fisica x
		where	x.cd_pessoa_fisica = a.cd_pessoa_fisica
		and	coalesce(x.ie_vinculo_profissional::text, '') = '');


BEGIN

open c01;
loop
fetch c01 into
	ie_vinculo_medico_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

update pessoa_fisica
set	ie_vinculo_profissional = ie_vinculo_medico_w
where	cd_pessoa_fisica = cd_pessoa_fisica_w
and	coalesce(ie_vinculo_profissional::text, '') = '';

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
