SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_acerto_vl_evento_dup () AS $body$
DECLARE

 
tb_vl_item_w		pls_util_cta_pck.t_number_table;
tb_nr_seq_evento_w	pls_util_cta_pck.t_number_table;

c01 CURSOR FOR 
	SELECT 	g.vl_item vl_item, 
		c.nr_sequencia seq_evento 
	from	pls_pagamento_item	g, 
		pls_pagamento_prestador	b, 
		pls_lote_pagamento 	a, 
		pls_evento_movimento	c 
	where	a.nr_sequencia		= b.nr_seq_lote 
	and  	a.dt_mes_competencia 	>= to_date('10/2015', 'mm/yyyy') 
	and	b.nr_sequencia		= g.nr_seq_pagamento 
	and	c.nr_seq_pagamento_item = g.nr_sequencia 
	and (SELECT	sum(k.vl_item) 
		from	pls_pagamento_item	k, 
			pls_pagamento_prestador	l, 
			pls_lote_pagamento 	m 
		where	m.nr_sequencia		= l.nr_seq_lote 
		and  	m.dt_mes_competencia 	>= to_date('10/2015', 'mm/yyyy') 
		and	l.nr_sequencia		= k.nr_seq_pagamento 
		and	k.nr_sequencia		= g.nr_sequencia 
		group by k.nr_seq_evento,l.nr_seq_prestador,m.nr_sequencia) 
		<> 
		(select	sum(n.vl_movimento) 
		from	pls_evento_movimento	n, 
			pls_pagamento_item	o, 
			pls_pagamento_prestador	p, 
			pls_lote_pagamento 	q 
		where	q.nr_sequencia		= p.nr_seq_lote 
		and  	q.dt_mes_competencia 	>= to_date('10/2015', 'mm/yyyy') 
		and	p.nr_sequencia		= o.nr_seq_pagamento 
		and	n.nr_seq_pagamento_item	= o.nr_sequencia 
		and	o.nr_sequencia		= g.nr_sequencia 
		group by o.nr_seq_evento,p.nr_seq_prestador,q.nr_sequencia);


BEGIN 
 
open c01;
loop 
 
	fetch c01 bulk collect into tb_vl_item_w, tb_nr_seq_evento_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_vl_item_w.count = 0;
	 
	forall i in tb_vl_item_w.first..tb_vl_item_w.last 
		update	pls_evento_movimento 
		set	vl_movimento = tb_vl_item_w(i) 
		where 	nr_sequencia = tb_nr_seq_evento_w(i);
 
	commit;
end loop;
close c01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_acerto_vl_evento_dup () FROM PUBLIC;

