CREATE OR REPLACE PROCEDURE baca_ajustar_apae (nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		bigint;
nr_seq_aval_pre_w	bigint;
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT		nr_seq_aval_pre
	from		exame_fisico_apae
	group by 	nr_seq_aval_pre
	having 		count(*) > 1;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	exame_fisico_apae;

if (qt_registro_w > 0) then

	open C01;
	loop
	fetch C01 into
		nr_seq_aval_pre_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	min(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	exame_fisico_apae
		where	nr_seq_aval_pre = nr_seq_aval_pre_w;

		update	exame_fisico_apae
		set	nr_seq_superior = nr_sequencia_w
		where	nr_seq_aval_pre = nr_seq_aval_pre_w
		and	nr_sequencia <> nr_sequencia_w;

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
