SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_atrib_contab () AS $body$
DECLARE


ie_compl_w		varchar(255);
nr_sequencia_w		trans_financ_contab.nr_sequencia%type;
nr_seq_trans_financ_w	trans_financ_contab.nr_sequencia%type;
nm_atributo_w		trans_financ_contab.nm_atributo%type;
nm_atrib_cadastro_w	trans_financ_contab.nm_atributo%type;
nm_atrib_alterado_w	trans_financ_contab.nm_atributo%type;

C01 CURSOR FOR
	SELECT	nm_atributo
	from	atributo_contab
	where	cd_tipo_lote_contab	= 30
	and	substr(nm_atributo, length(nm_atributo) -2, length(nm_atributo)) != '_EC';


C02 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_trans_financ,
		nm_atributo
	from	trans_financ_contab
	where	cd_tipo_lote_contabil	= 30
	and	substr(nm_atributo, length(nm_atributo) -2, length(nm_atributo)) != 'EC';


BEGIN
ie_compl_w	:= '_EC';

open C01;
loop
fetch C01 into
	nm_atrib_cadastro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update  atributo_contab
	set	nm_atributo		= nm_atrib_cadastro_w||ie_compl_w
	where	cd_tipo_lote_contab	= 30
	and	upper(nm_atributo)	= upper(nm_atrib_cadastro_w);
	end;
end loop;
close C01;

open C02;
loop
fetch C02 into
	nr_sequencia_w,
	nr_seq_trans_financ_w,
	nm_atributo_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	begin
	update	trans_financ_contab
	set	nm_atributo 		= nm_atributo_w||ie_compl_w
	where	nr_sequencia		= nr_sequencia_w
	and	nr_seq_trans_financ	= nr_seq_trans_financ_w;
	exception
	when others then
		null;
	end;
	end;
end loop;
close C02;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
