SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ajustar_autorizador_aih () AS $body$
DECLARE

 
C01 CURSOR FOR 
	SELECT	nr_atendimento, 
		nr_aih, 
		nr_sequencia 
	from	sus_aih_unif 
	where	somente_numero(coalesce(CD_MEDICO_AUTORIZADOR,'')) = 0;
	
	 
c01_w	c01%rowtype;
cd_medico_autorizador_w		varchar(10);
cd_orgao_emissor_w		varchar(200);

BEGIN
 
open C01;
loop 
fetch C01 into	 
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	SELECT * FROM sus_atualiza_aih_dados(c01_w.nr_atendimento, cd_medico_autorizador_w, cd_orgao_emissor_w) INTO STRICT cd_medico_autorizador_w, cd_orgao_emissor_w;
	if (somente_numero(coalesce(cd_medico_autorizador_w,''))	<> 0) then 
		update	sus_aih_unif 
		set	CD_MEDICO_AUTORIZADOR 	= cd_medico_autorizador_w 
		where	nr_aih		= c01_w.nr_aih 
		and	nr_sequencia   = c01_w.nr_sequencia;
	end if;
	 
	exception 
	when others then 
		cd_medico_autorizador_w:=cd_medico_autorizador_w;
	end;
end loop;
close C01;
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
