CREATE OR REPLACE PROCEDURE baca_ajustar_base_grupos_wscb () AS $body$
DECLARE


nr_seq_obj_wscb_w	bigint;
nr_seq_obj_grupo_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	dic_objeto
where	ie_tipo_objeto = 'CWSCB'
and	ie_tipo_grupo_obj_wscb = 'A'
order by
	nr_sequencia;


BEGIN
if (1 = 2) then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_obj_wscb_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		select	nextval('dic_objeto_seq')
		into STRICT	nr_seq_obj_grupo_w
		;

		insert into dic_objeto(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo_objeto,
			nr_seq_obj_sup,
			cd_funcao,
			ds_informacao,
			nr_seq_apres,
			nm_campo_base,
			qt_altura,
			ie_objeto_nulo,
			ie_objeto_generico,
			ie_read_only)
		SELECT	nr_seq_obj_grupo_w,
			clock_timestamp(),
			'Rafael',
			clock_timestamp(),
			'Rafael',
			'GCWSCB',
			nr_seq_obj_wscb_w,
			cd_funcao,
			ds_informacao,
			1,
			nm_campo_base,
			qt_altura,
			'N',
			'N',
			'N'
		from	dic_objeto
		where	nr_sequencia = nr_seq_obj_wscb_w;

		update	dic_objeto
		set	nr_seq_obj_sup = nr_seq_obj_grupo_w
		where	nr_seq_obj_sup = nr_seq_obj_wscb_w
		and	ie_tipo_objeto = 'APWSCB';
		end;
	end loop;
	close c01;
	end;
end if;
commit;
end;
